import java.util.Scanner;

public class Q10569 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int t = sc.nextInt();
		int v, e;
		for (int i = 0; i < t; i++) {
			v = sc.nextInt();
			e = sc.nextInt();
			System.out.println(2 - v + e);
		}
		sc.close();
	}

}
